import { useState } from 'react';

function App() {

  const [firstNumber, setFirstNumber] = useState(0);
  const [secondNumber, setSecondNumber] = useState(0);
  const [result, setResult] = useState(0);

  function handleClick(type, firstNumber, secondNumber) {
    const body = "type" + "=" + type + "&" 
    + "firstNumber" + "=" + firstNumber + "&"
    + "secondNumber" + "=" + secondNumber;

    const res = fetch("http://localhost:5000/", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: body
    })

    console.log(res.json());
  }

  function AddButton({ onClick }) {
    return (
      <button onClick={onClick("add", firstNumber, secondNumber)}>Add</button>
    );
  }

  function SubtractButton({ onClick }) {
    return (
      <button onClick={onClick("subtract", firstNumber, secondNumber)}>Subtract</button>
    );
  }

  function changeFirstNumber(event) {
    setFirstNumber(event.target.value)
  }

  function changeSecondNumber(event) {
    setSecondNumber(event.target.value)
  }

  return (
    <div>
        <input onChange={changeFirstNumber} value={firstNumber} />
        <input onChange={changeSecondNumber} value={secondNumber} />
        <AddButton onClick={(type, firstNumber, secondNumber) => handleClick} />
        <SubtractButton onClick={(type, firstNumber, secondNumber) => handleClick} />
        {result}
    </div>
  );
}

export default App;
