import { useState } from 'react';

function App() {

  var [firstNumber, setFirstNumber] = useState(0);
  var [secondNumber, setSecondNumber] = useState(0);
  var [result, setResult] = useState(0);

  function handleClick(type) {
    // Set input fields to 0 if they are blank
    firstNumber = firstNumber ? firstNumber : 0;
    secondNumber = secondNumber ? secondNumber : 0;

    const body = "type" + "=" + type + "&" 
    + "firstNumber" + "=" + firstNumber + "&"
    + "secondNumber" + "=" + secondNumber;

    console.log("type: " + type);
    console.log("firstNumber: " + firstNumber);
    console.log("secondNumber: " + secondNumber);

    fetch("http://localhost:5000/", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: body
    }).then((res, req) => res.json()).then((data) => {setResult(data.result)});
  }

  function AddButton({ onClick }) {
    return (
      <button onClick={onClick("add")}>Add</button>
    );
  }

  function SubtractButton({ onClick }) {
    return (
      <button onClick={onClick("subtract")}>Subtract</button>
    );
  }

  function changeFirstNumber(event) {
    setFirstNumber(event.target.value)
  }

  function changeSecondNumber(event) {
    setSecondNumber(event.target.value)
  }

  return (
    <div>
        <input 
          onChange={changeFirstNumber}
          value={firstNumber} />
        <input 
          onChange={changeSecondNumber}
          value={secondNumber} />
        <AddButton onClick={(type) => () => handleClick(type)} />
        <SubtractButton onClick={(type) => () => handleClick(type)} />
        {"result: " + result}
    </div>
  );
}

export default App;
